from Crypto.Util.number import *

R = RealField(1000)

x   = R(0.)
c = 2.

sin_x = R(sin(x))
cos_x = R(cos(x))   

M = matrix(QQ,[
    [sin_x, 1, 0, 0],
    [cos_x, 0, 1, 0],
    [c, 0, 0, 1]
])
M[:, 0] *= 2 ** 1000
M = M.LLL()
print(M)
for i in M:
    for j in i:
        print(long_to_bytes(int(abs(j))))